import java.util.*;
import java.io.*;

public class NetworkPacketDriver
{
    public static void main(String[] args) throws IOException 
	{
		//binary string encodings test cases
		String encode1 = "";
		String encode2 = "";
		String encode3 = "";
		String encode4 = "";
		String encode5 = "";
		String encode6 = "";
		String encode7 = "";
		String encode8 = "";
		String encode9 = "";
		String encode10 = "";
		String encodings [] = {encode1, encode2, encode3, encode4, encode5, encode6, encode7, encode8, encode9, encode10};
		
		//huffman avg bits per symbol test cases
		double huffmanAvg1 = 2.548;
		double huffmanAvg2 = 2.346;
		double huffmanAvg3 = 2.398;
		double huffmanAvg4 = 2.38;
		double huffmanAvg5 = 2.24;
		double huffmanAvg6 = 2.498;
		double huffmanAvg7 = 2.448;
		double huffmanAvg8 = 2.4696132596685083;
		double huffmanAvg9 = 2.553222302679218;
		double huffmanAvg10 = 2.598752598752599;
		double huffmanAvgs [] = {huffmanAvg1, huffmanAvg2, huffmanAvg3, huffmanAvg4, huffmanAvg5, huffmanAvg6, huffmanAvg7, huffmanAvg8, huffmanAvg9, huffmanAvg10};
		
		//compression ratios test cases
		double ratio1 = 1.1773940345368916;
		double ratio2 = 1.278772378516624;
		double ratio3 = 1.2510425354462051;
		double ratio4 = 1.2605042016806722;
		double ratio5 = 1.3392857142857142;
		double ratio6 = 1.2009607686148918;
		double ratio7 = 1.2254901960784315;
		double ratio8 = 1.2147651006711409;
		double ratio9 = 1.1749858196256382;
		double ratio10 = 1.1544;
		double ratios [] = {ratio1, ratio2, ratio3, ratio4, ratio5, ratio6, ratio7, ratio8, ratio9, ratio10};
		
		for(int fileNum = 1; fileNum <= 10; ++fileNum)
		{
			NetworkPacket encoder = new NetworkPacket();
			
			ArrayList<String> symbols = new ArrayList<>();
			
			try(Scanner scanner = new Scanner(new File("sensor_data_" + fileNum + ".txt"))) 
			{
				while(scanner.hasNext()) 
				{
					symbols.add(scanner.next());
				}
			}
			catch(FileNotFoundException e) 
			{
				System.out.println("Sensor Data File not found!");
				e.printStackTrace();
			}
			
			encoder.analyzeFrequencies(symbols);
			encoder.buildHuffmanTree();

			String studentEncoded = encoder.encode(symbols);			
			double studentHuffmanAvg = encoder.getHuffmanAvg(symbols);
			double studentRatio = encoder.getRatio(studentHuffmanAvg);
			
			boolean check = true;
			
			if(fileNum != 2)
				if(!(studentEncoded.equals(encodings[fileNum - 1])))
				{
					System.out.println("Encoding is incorrect for test case " + fileNum + ".");
					System.out.println("Dr. Steinberg's Answer: " + encodings[fileNum - 1] + ".");
					System.out.println("Student's Answer: " + studentEncoded + ".");
					check = false;
				}
			else //Test Case 2 Tiebreaker
				if(!(studentEncoded.equals(encodings[fileNum - 1])) && !(studentEncoded.equals("")))
				{
					System.out.println("Encoding is incorrect for test case " + fileNum + ".");
					System.out.println("Dr. Steinberg's Answer: " + encodings[fileNum - 1] + ".");
					System.out.println("Student's Answer: " + studentEncoded + ".");
					check = false;
				}
			
			if(!(studentHuffmanAvg == huffmanAvgs[fileNum - 1]))
			{
				System.out.println("Huffman Average is incorrect for test case " + fileNum + ".");
				System.out.println("Dr. Steinberg's Answer: " + huffmanAvgs[fileNum - 1] + ".");
				System.out.println("Student's Answer: " + studentHuffmanAvg + ".");
				check = false;
			}
			
			if(!(studentRatio == ratios[fileNum - 1]))
			{
				System.out.println("Compression Average is incorrect for test case " + fileNum + ".");
				System.out.println("Dr. Steinberg's Answer: " + ratios[fileNum - 1] + ".");
				System.out.println("Student's Answer: " + studentRatio + ".");
				check = false;
			}
			
			if(check)
				System.out.println("Test Case " + fileNum + " Passed!");
			else
				System.out.println("Test Case " + fileNum + " Failed!");
			
			System.out.println("******************************************");
		}
    }
}
